//  Creating a new Person includes creating new Paper Paths
//  to not only visually represent a map of this Person’s
//  facial landmarks, but they’re also very mathematically
//  useful because of Paper’s numerous vector-based methods!

//  Why make this a Class-like thing rather than a single?
//  Sure in the presentation demo we’re only dealing with one
//  Person, but for testing and debugging it’s been oh-so
//  useful to be able to quickly fire up multiple states and
//  compare things.

GMOJI.Person = function( raw ){


	//  Create an Array and stuff it with N number of Objects
	//  like so {x:0,y:0}. Why? Paper wants to retain the Paths
	//  we make indefinitely so instead of creating Paths per
	//  update / draw loop we create them once (here) with zero
	//  values and update those particular values later.

	function fillWithZeros( n ){

		var fill = []

		while( n -- ) fill.push({ x: 0, y: 0 })
		return fill
	}


	//  We’re about to make a ton of Paper Paths
	//  as well as Groups -- so we can easily rotate
	//  and move multiple Items at once.
	//  And we’ll also create convenience accessors
	//  for brevity and cleanliness ;)

	this.face = new paper.Group({ name: 'face' })


	//  Anchor point.

	this.anchor = new paper.Path.Circle({

		name:  'anchor',
		radius: 8,
		strokeWidth: 4
	})
	this.face.addChild( this.anchor )


	//  Eyes.

	this.eyeLeft = new paper.Path({

		name:    'eyeLeft',
		segments: fillWithZeros( 6 ),
		closed:   true
	})
	this.face.addChild( this.eyeLeft )
	this.eyeRight = new paper.Path({

		name:    'eyeRight',
		segments: fillWithZeros( 6 ),
		closed:   true
	})
	this.face.addChild( this.eyeRight )


	//  Eyebrows.

	this.eyebrowLeft = new paper.Path({

		name:    'eyebrowLeft',
		segments: fillWithZeros( 5 ),
		relativeSegments: fillWithZeros( 5 )
	})
	this.face.addChild( this.eyebrowLeft )
	this.eyebrowRight = new paper.Path({

		name:    'eyebrowRight',
		segments: fillWithZeros( 5 ),
		relativeSegments: fillWithZeros( 5 )
	})
	this.face.addChild( this.eyebrowRight )


	//  Mouth.

	this.mouth = {}
	this.face.addChild( this.mouth.outer = new paper.Path({

		name:     'outer',
		segments:  fillWithZeros( 12 ),
		relativeSegments: fillWithZeros( 12 ),
		closed:    true
	}))
	this.face.addChild( this.mouth.inner = new paper.Path({

		name:    'inner',
		segments: fillWithZeros( 8 ),
		relativeSegments: fillWithZeros( 8 ),
		closed:   true
	}))
	this.face.addChild( this.mouth.center = new paper.Path({

		name:    'center',
		segments: fillWithZeros( 3 ),
		relativeSegments: fillWithZeros( 3 ),
		closed:   false,
		dashArray: [ 2, 3 ]
	}))


	//  Nose.

	this.noseApex = new paper.Path({

		name:    'noseApex',
		segments: fillWithZeros( 5 ),
		relativeSegments: fillWithZeros( 5 )
	})
	this.face.addChild( this.noseApex )
	this.noseSpine = new paper.Path({

		name:    'noseSpine',
		segments: fillWithZeros( 4 ),
		relativeSegments: fillWithZeros( 4 )
	})
	this.face.addChild( this.noseSpine )


	//  Jaw.

	this.jaw = new paper.Path({

		name:    'jaw',
		segments: fillWithZeros( 17 ),
		relativeSegments: fillWithZeros( 17 )
	})
	this.face.addChild( this.jaw )


	//  We have to apply color AFTER adding all the child elements
	//  if we want it to be applied to all of them.

	this.face.strokeColor = '#3366CC'
	this.face.center = paper.view.bounds.center
	if( GMOJI.debug !== true ) this.face.visible = false


	//  We might as well make use of our test outputs
	//  as usable defaults.

	if( raw === undefined ) raw = GMOJI.Person.FACES.BLANK
	this.update( raw, false, true )
}




//  For Jonas it was convenient to export normalized coordinates
//  similar to X:0..1 = X:0..960, Y:0..1 = Y:0..1080.
// (The important thing here is the aspect ratio, not the exact
//  pixel values.) For me it’s more convenient if (0,0) is the
//  center of the canvas, not the upper left corner -- and if
//  the aspect ratio is square so we can can scale evenly on the
//  X and Y axes. So let’s get down to business!

GMOJI.Person.translateJonasToStewart = function( points, flipLeftRight ){

	return points.map( function( point ){


		//  I’d like to make scaling from center super easy.
		//  Jonas’ coordinates are x:0..1, y:0..1
		//  but I’d like to convert to -1..+1 respectively.

		point.x -= 0.5
		point.y -= 0.5


		//  Jonas is now using HALF of a 16:9 screen.
		//  Let’s convert his normalization to a 1:1 aspect
		//  ratio to make life easier.
		//  UPDATE! ALL LOGIC GOES OUT THE WINDOW!

		point.x *= 5//  2 * 16 / 9 / 2
		point.y *= 3//  2


		//  Wrap it up!

		if( flipLeftRight !== true ) point.x *= -1//  Last minute mirroring!
		return {

			x: point.x,
			y: point.y
		}
	})
}


//  Jonas later dropped in this little gem:

GMOJI.Person.normalizeLandmarks = function( points ){
	
	var 
	lEye = new paper.Point( points[ 45 ]),
	rEye = new paper.Point( points[ 36 ]),
	dist = lEye.getDistance( rEye ),
	mult = 0.5 / dist
	
	return points.map( function( point ){

		return  {
	
			x: point.x * mult,
			y: point.y * mult
		}
	})
}




//  Here we can receive the raw data coming from Jonas
//  and apply it to this Person’s face vectors.

GMOJI.Person.prototype.update = function( raw, translateCoords, flipLeftRight ){//  Expecting 68 values.

	var
	person = this,
	m = this.mouth,
	landmarks = raw.landmarks.slice(),//  Make a copy so as to leave original coords untouched.
	marks//  Temporary subset of landmarks for convenience.


	//  Here we’re going to assume that the number of points we’re receiving
	//  exactly matches the number of points we setup initially...
	//  Cross your fingers because it could get weird.

	function updateThis( element, points ){

		if( typeof element === 'string' ) element = person[ element ]
		points.forEach( function( point, i ){

			if( element.name !== 'center' ) point = GMOJI.plot( point )


			//  First, let’s put this point into our proper coordinate space.

			element.segments[ i ].point = new paper.Point( point )


			//  But next -- if this isn’t one of the eyes then we want to
			//  create points that are RELATIVE to the eyes.
			//  We’ll store these in a RELATIVE segments Array.

			if( element.name !== 'eyeLeft' && element.name !== 'eyeRight' ){

				element.relativeSegments[ i ].point = person.relativeTransform.transform( element.segments[ i ].point )
			}
		})
	}


	//  Paper.js can already tell us the literally boundaries of Paths
	//  but only relative to the screen’s axes -- there’s no concept of
	//  rotation or “un-rotation” so we’d better measure this ourselves.

	function getTrueBounds( element, top, right, bottom, left ){

		var
		width,
		height,
		angle

		if( typeof element === 'string' ) element = person[ element ]


		//  Let’s find our points; clockwise.

		top    = element.segments[ top    ].point
		right  = element.segments[ right  ].point
		bottom = element.segments[ bottom ].point
		left   = element.segments[ left   ].point


		//  Then the bounds.

		element.trueWidth  = left.getDistance( right )
		element.trueHeight = top.getDistance( bottom )
		element.trueAngle  = right.subtract( left ).angle * -1//  Mirrored and stuff...
		element.trueCenter = top.add( right ).add( bottom ).add( left ).divide( 4 )
	}


	//  Useful for debugging and exporting.

	if( translateCoords !== false ) landmarks = GMOJI.Person.translateJonasToStewart( landmarks, flipLeftRight )
	landmarks      = GMOJI.Person.normalizeLandmarks( landmarks )//  Jonas drop in: Normalize coordinates so eye distance is constant.
	this.raw       = raw
	this.landmarks = landmarks


	//  Orientation.
	//  3D vector pointing forward from the face.
	//  Example: ( 0, 0, 1 ) means the person is
	//  looking straight forward into the camera.

	this.orientation = raw.orientation


	//  Eye LEFT.

	if( flipLeftRight !== true ) marks = landmarks.slice( 42, 48 )
	else marks = landmarks.slice( 36, 42 )
	updateThis( 'eyeLeft', marks )
	this.eyeLeft.isOpen    = raw.leftEye
	this.eyeLeft.isClosed  = 1 - raw.leftEye//  For human convenience.
	getTrueBounds( 'eyeLeft', 1, 3, 5, 0 )


	//  Eye RIGHT.

	if( flipLeftRight !== true ) marks = landmarks.slice( 36, 42 )
	else marks = landmarks.slice( 42, 48 )
	updateThis( 'eyeRight', marks )
	this.eyeRight.isOpen   = raw.rightEye
	this.eyeRight.isClosed = 1 - raw.rightEye//  For human convenience.
	getTrueBounds( 'eyeRight', 2, 3, 4, 0 )


	//  Eyes RELATIVE TRANSFORM MATRIX.
	//  Now that we have the eye position data we can tranform global
	// “global” coordinates to relative ones -- specifically relative
	//  to this person’s eyes.

	this.relativeTransform = new paper.Matrix()
	this.left   = this.eyeLeft.trueCenter
	this.right  = this.eyeRight.trueCenter
	this.angle  = this.right.subtract( this.left ).angle
	if( this.angle < -90 ) this.angle = ( 180 + this.angle ) * -1// WTF is this necessary for SOME exported faces?!?!?!? (Namely SURPRISE)
	this.center = this.right.subtract( this.left ).divide( 2 ).add( this.left )
	this.scale  = this.left.getDistance( this.right ) / 100
	this.relativeTransform.scale( 1 / this.scale )
	this.relativeTransform.rotate( -this.angle, 0, 0 )
	this.relativeTransform.translate( this.left.multiply( -1 ))
	this.relativeTransform.translate( this.left.subtract( this.right ).divide( 2 ))
	this.anchor.position = this.center
	//this.anchor.scaleTo( this.scale )


	//  Eyebrows.

	if( flipLeftRight !== true ) marks = landmarks.slice( 22, 27 )
	else marks = landmarks.slice( 17, 22 )
	updateThis( 'eyebrowLeft',  marks )
	if( flipLeftRight !== true ) marks = landmarks.slice( 17, 22 )
	else marks = landmarks.slice( 22, 27 )
	updateThis( 'eyebrowRight', marks )


	//  Mouth DETECTORS.

	m.isSmilingSmall = raw.classifier_small_smile
	m.isSmilingBig   = raw.classifier_big_smile
	m.isFrowning     = raw.classifier_frown
	m.isSayingOh     = raw.classifier_o


	//  Mouth OUTER.

	updateThis( m.outer, landmarks.slice( 48, 60 ))
	getTrueBounds( m.outer, 3, 6, 9, 0 )


	//  Mouth INNER.

	updateThis( m.inner, landmarks.slice( 60, 68 ))
	getTrueBounds( m.inner, 2, 4, 6, 0 )


	//  Mouth CENTER LINE.
	//  This just helps make life easy.

	updateThis( m.center, [

		m.inner.segments[ 0 ].point,
		m.inner.segments[ 2 ].point.add( m.inner.segments[ 6 ].point ).divide( 2 ),
		m.inner.segments[ 4 ].point
	])


	//  Nose.

	updateThis( 'noseApex',  landmarks.slice( 31, 36 ))
	updateThis( 'noseSpine', landmarks.slice( 27, 31 ))


	//  Jaw.

	updateThis( 'jaw', landmarks.slice( 0, 17 ))
	getTrueBounds( 'jaw', 16, 16, 8, 0 )


	//  This last .bringToFront() ensures that when debug mode
	//  is on that this wireframe floats in front of the puppet.

	this.face.bringToFront()
	return this
}








//  Quick convenience methods for inspecting this Person.

GMOJI.Person.prototype.dumpAsJson = function(){

	console.log( JSON.stringify( this ))
}
GMOJI.Person.prototype.dumpAsObject = function(){

	console.log( this )
}
GMOJI.Person.prototype.dumpToDom = function(){

	document.getElementById( 'info' ).innerHTML = JSON.stringify( this )
}




